mapper.select:
select d.hxzid from sb_crane_addrecord d INNER JOIN sb_crane_binding b ON d.hxzid = b.hxzid
WHERE pid = #{pid}
GROUP BY d.hxzid


  
     and id = #{id}
      and device_no = #{deviceNo}
      and runtime = #{runtime}
      and `load` = #{load}
      and load_ratio = #{loadRatio}
      and moment = #{moment}
      and moment_ratio = #{momentRatio}
      and slewing_speed = #{slewingSpeed}
      and is_left_warning = #{isLeftWarning}
      and is_right_warning = #{isRightWarning}
      and `range` = #{range}
      and is_forward_warning = #{isForwardWarning}
      and is_backward_warning = #{isBackwardWarning}
      and height = #{height}
      and is_up_warning = #{isUpWarning}
      and work_environment = #{workEnvironment}
      and real_time_flag = #{realTimeFlag}
      and installation_method = #{installationMethod}
      and wind_speed = #{windSpeed}
      and card_num = #{cardNum}
      and operator_name = #{operatorName}
      and hxzid = #{hxzid}
      and magnification = #{magnification}
      and ratedWeight = #{ratedWeight}
 
order by runtime desc

mapper.update:
update sb_crane_addrecord
device_no = #{deviceNo}, runtime = #{runtime}, `load` = #{load}, load_ratio = #{loadRatio}, moment = #{moment}, moment_ratio = #{momentRatio}, slewing_speed = #{slewingSpeed}, is_left_warning = #{isLeftWarning}, is_right_warning = #{isRightWarning}, `range` = #{range}, is_forward_warning = #{isForwardWarning}, is_backward_warning = #{isBackwardWarning}, height = #{height}, is_up_warning = #{isUpWarning}, work_environment = #{workEnvironment}, real_time_flag = #{realTimeFlag}, installation_method = #{installationMethod}, wind_speed = #{windSpeed}, card_num = #{cardNum}, operator_name = #{operatorName}, hxzid = #{hxzid}, magnification = #{magnification}, ratedWeight = #{ratedWeight},
where id = #{id}

mapper.select:
select count(*) from sb_crane_addrecord where TO_DAYS(runtime) =TO_DAYS(#{time})  and (is_left_warning=1 or is_right_warning=1 or is_forward_warning =1 or is_backward_warning=1 or is_up_warning=1)
 and hxzid in (select hxzid from sb_crane_binding where pid=${pid})
 and hxzid =#{hxzid}

select count(*) from sb_crane_addrecord where TO_DAYS(runtime) =TO_DAYS(#{time})  and load_ratio>1
 and hxzid in (select hxzid from sb_crane_binding where pid=${pid})
 and hxzid =#{hxzid}


where hxzid=#{hxzid}

and to_days(runtime)=to_days(#{time})


and load_ratio ${status}

order by runtime desc
